import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

const Login = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: ""
  });
  const [isLoading, setIsLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      // Validasi input
      if (!formData.email || !formData.password) {
        alert("Please fill all fields!");
        setIsLoading(false);
        return;
      }

      console.log("Login attempt:", { email: formData.email });

      // Kirim request login ke backend
      const response = await axios.post("http://localhost:5000/auth/login", {
        email: formData.email.trim(),
        password: formData.password
      });

      // Simpan token dan user data
      const { token, user } = response.data;
      localStorage.setItem("token", token);
      localStorage.setItem("user", JSON.stringify(user));

      // Set default axios header untuk request selanjutnya
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      alert(`Welcome back, ${user.fullName}!`);
      navigate("/users");
    } catch (error) {
      console.log("Login error:", error);
      
      if (error.response && error.response.data && error.response.data.message) {
        alert(`Login failed: ${error.response.data.message}`);
      } else {
        alert("Login failed. Please check your credentials and try again.");
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div 
      className="section" 
      style={{
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center'
      }}
    >
      <div className="container">
        <div className="columns is-centered">
          <div className="column is-4">
            <div className="box" style={{ borderRadius: '15px', boxShadow: '0 8px 32px rgba(0,0,0,0.1)' }}>
              {/* Header */}
              <div className="has-text-centered mb-5">
                <h1 className="title is-3 has-text-primary">
                  <span className="icon is-large">
                    <i className="fas fa-user-circle"></i>
                  </span>
                </h1>
                <h2 className="title is-4">Welcome Back</h2>
                <p className="subtitle is-6 has-text-grey">Please sign in to your account</p>
              </div>

              {/* Login Form */}
              <form onSubmit={handleSubmit}>
                {/* Email Field */}
                <div className="field">
                  <label className="label">Email Address</label>
                  <div className="control has-icons-left">
                    <input
                      className="input"
                      type="email"
                      name="email"
                      placeholder="Enter your email"
                      value={formData.email}
                      onChange={handleChange}
                      required
                    />
                    <span className="icon is-small is-left">
                      <i className="fas fa-envelope"></i>
                    </span>
                  </div>
                </div>

                {/* Password Field */}
                <div className="field">
                  <label className="label">Password</label>
                  <div className="control has-icons-left has-icons-right">
                    <input
                      className="input"
                      type={showPassword ? "text" : "password"}
                      name="password"
                      placeholder="Enter your password"
                      value={formData.password}
                      onChange={handleChange}
                      required
                    />
                    <span className="icon is-small is-left">
                      <i className="fas fa-lock"></i>
                    </span>
                    <span 
                      className="icon is-small is-right is-clickable"
                      onClick={() => setShowPassword(!showPassword)}
                      style={{ pointerEvents: 'all' }}
                    >
                      <i className={`fas ${showPassword ? 'fa-eye-slash' : 'fa-eye'}`}></i>
                    </span>
                  </div>
                </div>

                {/* Remember Me & Forgot Password */}
                <div className="field">
                  <div className="level">
                    <div className="level-left">
                      <label className="checkbox">
                        <input type="checkbox" />
                        {" "}Remember me
                      </label>
                    </div>
                    <div className="level-right">
                      <a href="#" className="has-text-link is-size-7">Forgot Password?</a>
                    </div>
                  </div>
                </div>

                {/* Submit Button */}
                <div className="field">
                  <button 
                    type="submit" 
                    className={`button is-primary is-fullwidth ${isLoading ? 'is-loading' : ''}`}
                    disabled={isLoading}
                  >
                    <span className="icon">
                      <i className="fas fa-sign-in-alt"></i>
                    </span>
                    <span>{isLoading ? 'Signing In...' : 'Sign In'}</span>
                  </button>
                </div>
              </form>

              {/* Divider */}
              <hr />

              {/* Register Link */}
              <div className="has-text-centered">
                <p>
                  Don't have an account?{" "}
                  <Link to="/register" className="has-text-link has-text-weight-semibold">
                    Create one here
                  </Link>
                </p>
              </div>
            </div>

            {/* Demo Credentials */}
            <div className="notification is-info is-light mt-4">
              <p className="has-text-weight-semibold">Demo Credentials:</p>
              <p><strong>Email:</strong> admin@example.com</p>
              <p><strong>Password:</strong> admin123</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
